    /*
     * Deleting report_user
     */
    function remove($id)
    {
        $report_user = $this->Report_user_model->get_report_user($id);

        // check if the report_user exists before trying to delete it
        if(isset($report_user['id']))
        {
            $this->Report_user_model->delete_report_user($id);
